mapper.update:
UPDATE ${tablePrefix}code
SET ref_id = #{refId}, updated_by = #{userId}
WHERE code = #{code} AND is_deleted = 0

UPDATE ${tablePrefix}code
SET available_times = #{avail}, remain_times = #{remain}, updated_by = #{userId}
WHERE code = #{code} AND is_deleted = 0

UPDATE ${tablePrefix}code
SET limit_times = #{times}, updated_by = #{userId}
WHERE code = #{code} AND is_deleted = 0

UPDATE ${tablePrefix}code
SET expired_at = #{expireTime}, updated_by = #{userId}
WHERE code = #{code} AND is_deleted = 0

UPDATE ${tablePrefix}code
SET is_deleted = 1, updated_by = #{userId}
WHERE code = #{code} AND is_deleted = 0

mapper.select:
SELECT vc.type, vc.code, vc.available_times, vc.remain_times, vc.limit_times, vc.expired_at AS expireTime, vc.created_at, vc.updated_at, vu.nick_name AS creator, vu2.nick_name AS updater, vca.name AS activityName, vcct.comment AS templateComment, vu3.nick_name AS assignUser FROM ${tablePrefix}code vc LEFT JOIN ${tablePrefix}user vu ON vc.created_by = vu.id LEFT JOIN ${tablePrefix}user vu2 ON vc.updated_by = vu2.id LEFT JOIN ${tablePrefix}user vu3 ON vc.assign_user_id = vu3.id LEFT JOIN ${tablePrefix}code_activity vca ON vc.activity_id = vca.id LEFT JOIN ${tablePrefix}code_coupon_template vcct ON vc.ref_id = vcct.id WHERE vc.type != 1 AND vc.is_deleted = 0 AND vc.activity_id IS NOT NULL
AND vc.type = #{type}
AND vc.activity_id = #{activityId}
ORDER BY vc.id DESC
