mapper.select:
SELECT status
FROM ${tablePrefix}billing_social_dingtalk_order
WHERE order_id = #{orderId}
AND tenant_id = #{tenantId}
AND app_id = #{appId}

SELECT order_data
FROM ${tablePrefix}billing_social_dingtalk_order
WHERE tenant_id = #{tenantId}
AND app_id = #{appId}
AND `status` = 0;

mapper.update:
UPDATE ${tablePrefix}billing_social_dingtalk_order
SET `status` = #{status}
WHERE tenant_id = #{tenantId}
AND app_id = #{appId}
AND `order_id` = #{orderId};

mapper.select:
SELECT order_id
FROM ${tablePrefix}billing_social_dingtalk_order
WHERE tenant_id = #{tenantId}
AND app_id = #{appId}
AND item_code = #{itemCode};
