mapper.select:
SELECT product_category, plan_id, metadata, expire_date
FROM ${tablePrefix}billing_subscription
WHERE space_id = #{spaceId}
AND state = #{state.name}
AND TIMESTAMPDIFF(SECOND,NOW(), expire_date) >= 0
AND is_deleted = 0

SELECT COUNT(*)
FROM ${tablePrefix}billing_subscription
WHERE space_id = #{spaceId}
AND plan_id = #{planId}
AND state = #{state}
AND TIMESTAMPDIFF(SECOND,NOW(), expire_date) >= 0
AND is_deleted = 0
AND metadata IS NOT NULL

SELECT COUNT(*)
FROM ${tablePrefix}billing_subscription
WHERE space_id = #{spaceId}
AND product_category = #{category.name}
AND state = #{state.name}
AND TIMESTAMPDIFF(SECOND,NOW(), expire_date) >= 0
AND is_deleted = 0

SELECT subscription_id
FROM ${tablePrefix}billing_subscription
WHERE space_id = #{spaceId}
AND phase = #{phase}
AND TIMESTAMPDIFF(SECOND, NOW(), expire_date) >= 0
ORDER BY created_at DESC LIMIT 1

SELECT count(*) FROM ${tablePrefix}billing_subscription WHERE bundle_id IN
#{item}
AND is_deleted = 0

SELECT bundle_id FROM ${tablePrefix}billing_subscription WHERE subscription_id IN
#{item}
AND is_deleted = 0
